import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/mealItem";
import Card from "../UI/card";

const DUMMY_MEALS = [
    {
        id: "m1",
        name: "Milanesa Napolitana",
        description:
            "Milanesa de carne con salsa de tomate con albahaca, orégano, jamón y queso. Acompañada con porcion de papas fritas",
        price: 3.5,
    },
    {
        id: "m2",
        name: "Ravioles con salsa de champignones",
        description:
            "Ravioles caseros de verdura con salsa de crema, verdeo y champignones",
        price: 4.0,
    },
    {
        id: "m3",
        name: "Lasagna",
        description: "Lasagna con picada, jamón y queso.",
        price: 3.0,
    },
    {
        id: "m4",
        name: "Godly ensalada",
        description:
            "Tomates cherris, lechuga, zanahoria, cebolla de verdeo, rúcula, pan tostado, queso, queso roquefort y aceite de oliva",
        price: 2.5,
    },
];

const AvailableMeals = () => {
    const mealsList = DUMMY_MEALS.map((meal) => (
        <MealItem
            id={meal.id}
            key={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    ));

    return (
        <section className={classes.meals}>
            <Card>
                <ul>{mealsList}</ul>
            </Card>
        </section>
    );
};

export default AvailableMeals;
